/*!

=========================================================
* Argon Dashboard React - v1.1.0
=========================================================

* Product Page: https://www.creative-tim.com/product/argon-dashboard-react
* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/argon-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React, { useState } from "react";
import { useTable } from "react-table";

// reactstrap components
import {
	Badge,
	Button,
	Card,
	CardHeader,
	CardFooter,
	DropdownMenu,
	DropdownItem,
	UncontrolledDropdown,
	DropdownToggle,
	Media,
	Pagination,
	PaginationItem,
	PaginationLink,
	Progress,
	Table,
	Container,
	Row,
	UncontrolledTooltip,
} from "reactstrap";
// core components
import Header from "components/Headers/Header.js";

const EditableCell = ({
	value: initialValue,
	row: { index },
	column: { id },
	updateMyData, // This is a custom function that we supplied to our table instance
}) => {
	// We need to keep and update the state of the cell normally
	const [value, setValue] = React.useState(initialValue);

	const onChange = (e) => {
		setValue(e.target.value);
	};

	//   We'll only update the external data when the input is blurred
	const onBlur = () => {
		updateMyData(index, id, value);
	};

	// If the initialValue is changed external, sync it up with our state
	React.useEffect(() => {
		setValue(initialValue);
	}, [initialValue]);

	//removed onBlur={onBlur}

	return <input value={value} onChange={onChange} onBlur={onBlur} />;
};

const IndeterminateCheckbox = React.forwardRef(
	({ indeterminate, ...rest }, ref) => {
		const defaultRef = React.useRef();
		const resolvedRef = ref || defaultRef;
		const [value, setValue] = useState({ ...rest }.checked);
		React.useEffect(() => {
			resolvedRef.current.indeterminate = indeterminate;
		}, [resolvedRef, indeterminate]);

		const handleValue = () => {
			if (value) setValue(false);
			else setValue(true);
		};
		return (
			<>
				<Button
					type="checkbox"
					ref={resolvedRef}
					{...rest}
					onClick={handleValue}
					value={value}
				>
					{value ? "CheckedIn" : "Checkout"}
				</Button>
			</>
		);
	}
);

function App({ columns, data, updateMyData }) {
	const {
		getTableProps,
		getTableBodyProps,
		headerGroups,
		prepareRow,
		page,
		// canPreviousPage,
		// canNextPage,
		// pageOptions,
		// pageCount,
		// gotoPage,
		// nextPage,
		// previousPage,
		// setPageSize,
		// state: { pageIndex, pageSize },
	} = useTable(
		{
			columns,
			data,
			updateMyData,
		},
		(hooks) => {
			hooks.visibleColumns.push((columns) => [
				{
					id: "selection",
					Header: () => <div>CheckIn/Checkout</div>,
					Cell: ({ row }) => (
						<div>
							<IndeterminateCheckbox {...row.getToggleRowSelectedProps()} />
						</div>
					),
				},
				...columns,
			]);
		}
	);
	return (
		<>
			<Header />
			{/* Page content */}
			<Container className="mt--7" fluid>
				{/* Table */}
				<Row>
					<div className="col">
						<Card className="shadow">
							<CardHeader className="border-0">
								<h3 className="mb-0">Card tables</h3>
							</CardHeader>
							<Table
								className="align-items-center table-flush"
								responsive
								{...getTableProps()}
							>
								<thead>
									{headerGroups.map((headerGroup) => (
										<tr {...headerGroup.getHeaderGroupProps()}>
											{headerGroup.headers.map((column) => (
												<th {...column.getHeaderProps()}>
													{column.render("Header")}
												</th>
											))}
										</tr>
									))}
								</thead>
								<tbody {...getTableBodyProps()}>
									{page.map((row, i) => {
										prepareRow(row);
										return (
											<tr id={`${row.id}_row`} {...row.getRowProps()}>
												{row.cells.map((cell) => {
													return (
														<td {...cell.getCellProps()}>
															{cell.render("Cell")}
														</td>
													);
												})}
											</tr>
										);
									})}
									<tr>
										<th scope="row">
											<Media className="align-items-center">
												<a
													className="avatar rounded-circle mr-3"
													href="#pablo"
													onClick={(e) => e.preventDefault()}
												>
													<img
														alt="..."
														src={require("assets/img/theme/bootstrap.jpg")}
													/>
												</a>
												<Media>
													<span className="mb-0 text-sm">
														Argon Design System
													</span>
												</Media>
											</Media>
										</th>
										<td>$2,500 USD</td>
										<td>
											<Badge color="" className="badge-dot mr-4">
												<i className="bg-warning" />
												pending
											</Badge>
										</td>
										<td>
											<div className="avatar-group">
												<a
													className="avatar avatar-sm"
													href="#pablo"
													id="tooltip742438047"
													onClick={(e) => e.preventDefault()}
												>
													<img
														alt="..."
														className="rounded-circle"
														src={require("assets/img/theme/team-1-800x800.jpg")}
													/>
												</a>
												<UncontrolledTooltip
													delay={0}
													target="tooltip742438047"
												>
													Ryan Tompson
												</UncontrolledTooltip>
												<a
													className="avatar avatar-sm"
													href="#pablo"
													id="tooltip941738690"
													onClick={(e) => e.preventDefault()}
												>
													<img
														alt="..."
														className="rounded-circle"
														src={require("assets/img/theme/team-2-800x800.jpg")}
													/>
												</a>
												<UncontrolledTooltip
													delay={0}
													target="tooltip941738690"
												>
													Romina Hadid
												</UncontrolledTooltip>
												<a
													className="avatar avatar-sm"
													href="#pablo"
													id="tooltip804044742"
													onClick={(e) => e.preventDefault()}
												>
													<img
														alt="..."
														className="rounded-circle"
														src={require("assets/img/theme/team-3-800x800.jpg")}
													/>
												</a>
												<UncontrolledTooltip
													delay={0}
													target="tooltip804044742"
												>
													Alexander Smith
												</UncontrolledTooltip>
												<a
													className="avatar avatar-sm"
													href="#pablo"
													id="tooltip996637554"
													onClick={(e) => e.preventDefault()}
												>
													<img
														alt="..."
														className="rounded-circle"
														src={require("assets/img/theme/team-4-800x800.jpg")}
													/>
												</a>
												<UncontrolledTooltip
													delay={0}
													target="tooltip996637554"
												>
													Jessica Doe
												</UncontrolledTooltip>
											</div>
										</td>
										<td>
											<div className="d-flex align-items-center">
												<span className="mr-2">60%</span>
												<div>
													<Progress
														max="100"
														value="60"
														barClassName="bg-danger"
													/>
												</div>
											</div>
										</td>
										<td className="text-right">
											<UncontrolledDropdown>
												<DropdownToggle
													className="btn-icon-only text-light"
													href="#pablo"
													role="button"
													size="sm"
													color=""
													onClick={(e) => e.preventDefault()}
												>
													<i className="fas fa-ellipsis-v" />
												</DropdownToggle>
												<DropdownMenu className="dropdown-menu-arrow" right>
													<DropdownItem
														href="#pablo"
														onClick={(e) => e.preventDefault()}
													>
														Action
													</DropdownItem>
													<DropdownItem
														href="#pablo"
														onClick={(e) => e.preventDefault()}
													>
														Another action
													</DropdownItem>
													<DropdownItem
														href="#pablo"
														onClick={(e) => e.preventDefault()}
													>
														Something else here
													</DropdownItem>
												</DropdownMenu>
											</UncontrolledDropdown>
										</td>
									</tr>
								</tbody>
							</Table>
							<CardFooter className="py-4">
								<nav aria-label="...">
									<Pagination
										className="pagination justify-content-end mb-0"
										listClassName="justify-content-end mb-0"
									>
										<PaginationItem className="disabled">
											<PaginationLink
												href="#pablo"
												onClick={(e) => e.preventDefault()}
												tabIndex="-1"
											>
												<i className="fas fa-angle-left" />
												<span className="sr-only">Previous</span>
											</PaginationLink>
										</PaginationItem>
										<PaginationItem className="active">
											<PaginationLink
												href="#pablo"
												onClick={(e) => e.preventDefault()}
											>
												1
											</PaginationLink>
										</PaginationItem>
										<PaginationItem>
											<PaginationLink
												href="#pablo"
												onClick={(e) => e.preventDefault()}
											>
												2 <span className="sr-only">(current)</span>
											</PaginationLink>
										</PaginationItem>
										<PaginationItem>
											<PaginationLink
												href="#pablo"
												onClick={(e) => e.preventDefault()}
											>
												3
											</PaginationLink>
										</PaginationItem>
										<PaginationItem>
											<PaginationLink
												href="#pablo"
												onClick={(e) => e.preventDefault()}
											>
												<i className="fas fa-angle-right" />
												<span className="sr-only">Next</span>
											</PaginationLink>
										</PaginationItem>
									</Pagination>
								</nav>
							</CardFooter>
						</Card>
					</div>
				</Row>
				{/* Dark table */}
			</Container>
		</>
	);
}

function Tables() {
	const columns = React.useMemo(
		() => [
			{
				Header: "Name",
				accessor: "name",
			},
			{
				Header: "UserName",
				accessor: "username",
			},

			{
				Header: "Email",
				accessor: "email",
			},
			{
				Header: "Website",
				accessor: "website",
			},
			{
				Header: "Profile Progress",
				Cell: EditableCell,
			},
		],
		[]
	);

	const [data, setData] = React.useState([]);

	const updateMyData = (rowIndex, columnId, value) => {
		setData((old) =>
			old.map((row, index) => {
				if (index === rowIndex) {
					return {
						...old[rowIndex],
						[columnId]: value,
					};
				}
				return row;
			})
		);
		console.log(rowIndex, columnId, value);
	};

	React.useEffect(() => {
		fetch("https://jsonplaceholder.typicode.com/users")
			.then((res) => res.json())
			.then((res) =>
				setData(
					res.map((item) => {
						return {
							name: item.name,
							username: item.username,
							email: item.email,
							website: item.website,
						};
					})
				)
			);
	});
	return (
		<React.Fragment>
			<App columns={columns} data={data} updateMyData={updateMyData} />
		</React.Fragment>
	);
}

export default Tables;
